var util = require('util');

var gps = exports = module.exports = {};

gps.init = function(){
  console.log('initializing gps module')
  this.settings = {};
  this.defaultConfiguration();
};

gps.set = function(setting, val){
  if (1 == arguments.length) {
    return this.settings[setting];
  } else {
    this.settings[setting] = val;
    //console.log('set: ['+setting+'] = ' + util.inspect(this.settings[setting]) );
    return this;
  }
};

gps.enabled = function(setting){
  return !!this.set(setting);
};

gps.disabled = function(setting){
  return !this.set(setting);
};

gps.enable = function(setting){
  return this.set(setting, true);
};

gps.disable = function(setting){
  return this.set(setting, false);
};

gps.defaultConfiguration = function() {
	gps.set('core', module.parent)
	console.log('io: '+util.inspect(this.settings.core.io));
};

gps.worker = function() {

}